import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import not from "../../images/noti.png";
import "./profil.css";

const AppointmentDetails = () => {
  const [appointment, setAppointment] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

const [appointments, setAppointments] = useState([]);


const handleDelete = async (id) => {
  try {
    const token = localStorage.getItem("token");
    if (!token) {
      setError("Token not found in local storage");
      return;
    }

    const config = {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    };

    const response = await axios.delete(
      `https://avocat-backend.onrender.com/api/appointment/user/${id}`,
      config
    );

    alert("Rendez-vous supprimé !");
    // Supprime le rendez-vous du state
    setAppointments(prev => prev.filter(app => app._id !== id));
  } catch (error) {
    alert("Erreur lors de la suppression");
  }
};


  return (
    <div className="appcontainer">
      <div className="headerContainer">
        <h2>Mes Rendez-vous</h2>

        <Link to="/reminder">
          <img src={not} alt="reminder" className="profil" />
        </Link>
      </div>
{loading ? (
  <div>Loading...</div>
) : appointments.length > 0 ? (
  appointments.map((appointment, index) => (
    <div key={index} className="appdatetime">
      <div><b>Date:</b> {new Date(appointment.date).toLocaleDateString()}</div>
      <div><b>Heure:</b> {appointment.time}</div>
      <div><b>Status:</b> {appointment.status}</div>

      {appointment.status === "denied" && (
        <div className="alert alert-danger">
          <b>Message:</b> {appointment.statusMessage}
        </div>
      )}
      {appointment.status === "confirmed" && (
        <div className="alert alert-success">
          <b>Paiement:</b> unvalid
        </div>
      )}

      <div className="op">
        <Link to="/EditAppointment">
          <button className="Folssa">Modifier</button>
        </Link>
        <button className="Folssa" onClick={() => handleDelete(appointment._id)}>
          Annuler
        </button>
      </div>
    </div>
  ))
) : (
  <div>Aucun rendez-vous trouvé.</div>
)}

    </div>
  );
};

export default AppointmentDetails;
